    base: {
      color: "#32325d",
      fontFamily: "Arial, sans-serif",
      fontSmoothing: "antialiased",
      fontSize: "16px",
      "::placeholder": {
        color: "#32325d",
      },
      ":focus": {
        color: "white !important",
      },
    },
    valid: {
      color: "#fa755a",
    },
    invalid: {
      color: "#fa755a",
      iconColor: "#fa755a",
    },
  },
};
const CheckoutForm = () => {
  const handleSubmit = (e) => {
    return null;
  };

  return (
    <form
      id={"stripe-checkout-form"}
      onSubmit={handleSubmit}
      style={{ color: "white" }}
    >
      <CardElement id="card-element" option={cardStyle}></CardElement>
    </form>
  );
};

export default CheckoutForm;
