export function useCheckout() {
  this.progressing = false;
  this.error = null;
  this.success = false;

  const [proccessing, setProccessing] = useState(this.proccessing);
  const [error, setError] = useState(this.error);
  const [success, setSuccess] = useState(this.success);
  useEffect(() => {
    setProccessing(this.progressing);
    setError(this.error);
    setSuccess(this.success);
  }, [this.progressing, this.error, this.success, proccessing, error, success]);
}
