import React, { useEffect, useState } from "react";
import { CardElement, useElements, useStripe } from "@stripe/react-stripe-js";
import { useFetch } from "../../hooks/useFetch";

const cardStyle = {
  style: {
    base: {
      fontSize: "16px",
      color: "#424770",
      "::placeholder": {
        color: "#aab7c4",
      },
    },
    invalid: {
      color: "#9e2146",
    },
  },
};

const products = "https://golf-company.herokuapp.com/api/golfs/";
const CheckoutForm = () => {
  const stripe = useStripe();
  const elements = useElements();
  const [clientSecret, setClientSecret] = useState("");
  console.log(clientSecret);
  const { data, loading, error } = useFetch(products);

  useEffect(() => {
    if (loading || error) return;
    const { price } = data[0];
    console.log(price);
    // fetch stripe client secret using to proccess payment
    window
      .fetch("/api/payments/stripe", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ items: [{ price, amount: 3 }] }),
      })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setClientSecret(data.clientSecret);
        console.log(data.clientSecret);
      });
  }, [data, loading, error]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    // return if stripe has not been loaded yet
    if (!stripe || elements) {
      return;
    }

    const cardElement = elements.getElement(CardElement);

    // Complete payment
    const { error, paymentMethod } = await stripe.createPaymentMethod({
      type: "card",
      card: cardElement,
    });

    if (error) {
      console.log("[error]", error);
    } else {
      console.log("[PaymentMethod]", paymentMethod);
    }
  };

  return (
    <form id={"stripe-checkout-form"} onSubmit={handleSubmit}>
      <CardElement id="card-element" options={cardStyle}></CardElement>
      <button type="submit" disabled={!stripe}>
        Pay
      </button>
    </form>
  );
};

export default CheckoutForm;
